import React, { useState, useEffect, useRef, useCallback } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Sidebar from './components/Sidebar';
import ChatWindow from './components/ChatWindow';
import ThemeToggle from './components/ThemeToggle';
import { useMobile } from './hooks/useMobile';

function App() {
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const isMobile = useMobile();
  const sidebarRef = useRef(null);

  // set initial theme and open sidebar on desktop
  useEffect(() => {
    const savedTheme = localStorage.getItem('theme');
    if (savedTheme === 'dark') {
      setIsDarkMode(true);
      document.documentElement.classList.add('dark');
    } else {
      setIsDarkMode(false);
      document.documentElement.classList.remove('dark');
    }

    if (!isMobile) {
      setIsSidebarOpen(true);
    }
  }, [isMobile]);

  // compute & set sidebar pixel width into CSS custom property
  const updateSidebarWidth = useCallback(() => {
    // if mobile or sidebar closed: collapsed width 80px
    if (isMobile || !isSidebarOpen) {
      document.documentElement.style.setProperty('--sidebar-px', '80px');
      return;
    }

    const el = sidebarRef.current;
    if (el) {
      // use offsetWidth so we get exact px value
      const w = el.offsetWidth || 280;
      document.documentElement.style.setProperty('--sidebar-px', `${w}px`);
    } else {
      document.documentElement.style.setProperty('--sidebar-px', '280px');
    }
  }, [isMobile, isSidebarOpen]);

  useEffect(() => {
    // update on mount + when sidebar open/close changes + on resize
    updateSidebarWidth();
    window.addEventListener('resize', updateSidebarWidth);
    return () => window.removeEventListener('resize', updateSidebarWidth);
  }, [updateSidebarWidth]);

  const toggleTheme = () => {
    const newTheme = !isDarkMode;
    setIsDarkMode(newTheme);
    if (newTheme) {
      document.documentElement.classList.add('dark');
      localStorage.setItem('theme', 'dark');
    } else {
      document.documentElement.classList.remove('dark');
      localStorage.setItem('theme', 'light');
    }
  };

  const toggleSidebar = () => {
    setIsSidebarOpen(prev => !prev);
  };

  const closeSidebar = () => {
    if (isMobile) {
      setIsSidebarOpen(false);
    }
  };

  return (
    <Router>
      <div className={`app min-h-screen w-full ${isDarkMode ? 'text-white' : 'text-gray-900'} ${isSidebarOpen && !isMobile ? 'sidebar-open' : ''}`}>
        {/* Sidebar receives a ref so we can measure it on desktop */}
        <Sidebar
          ref={sidebarRef}
          isOpen={isSidebarOpen}
          onToggle={toggleSidebar}
          onClose={closeSidebar}
          isDarkMode={isDarkMode}
          isMobile={isMobile}
        />

        {/* Main Content */}
        <div className="app-main">
          {/* Header - inner wrapped inside content-column so it lines up correctly */}
          <header className={`glass border-b ${isDarkMode ? 'border-gray-700' : 'border-gray-200'} p-4 sticky top-0 z-40`}>
            <div className="content-column">
              <div className="inner flex justify-between items-center">
                <div className="flex items-center space-x-3">
                  <button
                    onClick={toggleSidebar}
                    className={`p-3 rounded-2xl smooth-transition hover-lift ${isDarkMode ? 'bg-gray-700 text-white' : 'bg-white text-gray-700 shadow-sm'}`}
                    aria-label="Toggle sidebar"
                  >
                    <div className="w-5 h-5 flex flex-col justify-center items-center">
                      <div className={`w-4 h-0.5 bg-current rounded-full transition-all duration-300 ${isSidebarOpen ? 'rotate-45 translate-y-0.5' : ''}`}></div>
                      <div className={`w-4 h-0.5 bg-current rounded-full transition-all duration-300 mt-1 ${isSidebarOpen ? '-rotate-45 -translate-y-0.5' : ''}`}></div>
                    </div>
                  </button>

                  <div className="flex items-center space-x-3">
                    <div className={`w-8 h-8 rounded-2xl flex items-center justify-center font-bold text-sm ${isDarkMode ? 'bg-gradient-to-r from-purple-500 to-blue-500 text-white shadow-lg' : 'bg-gradient-to-r from-blue-500 to-purple-500 text-white shadow-lg'}`}>
                      L
                    </div>
                    <h1 className="text-xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">Lumibyte AI</h1>
                  </div>
                </div>

                <div className="flex items-center space-x-3">
                  <div className={`px-3 py-1 rounded-full text-xs font-medium ${isDarkMode ? 'bg-green-500/20 text-green-400' : 'bg-green-100 text-green-700'}`}>Online</div>
                  <ThemeToggle isDarkMode={isDarkMode} toggleTheme={toggleTheme} />
                </div>
              </div>
            </div>
          </header>

          {/* Routes content inside same content-column so header/cards/messages align */}
          <main className="flex-1 overflow-hidden">
            <div className="content-column">
              <div className="inner h-full w-full">
                <Routes>
                  <Route path="/" element={
                    <div className="flex items-center justify-center h-full w-full p-4">
                      <div className="text-center w-full max-w-md animate-fade-in">
                        <div className="floating mb-6">
                          <div className={`w-16 h-16 mx-auto rounded-2xl flex items-center justify-center text-3xl ${isDarkMode ? 'bg-gradient-to-r from-purple-500 to-blue-500 text-white shadow-2xl' : 'bg-gradient-to-r from-blue-500 to-purple-500 text-white shadow-2xl'}`}>ðŸ’¬</div>
                        </div>
                        <h2 className="text-2xl font-bold mb-3 text-gradient">Welcome to Lumibyte</h2>
                        <p className={`text-sm mb-6 ${isDarkMode ? 'text-gray-300' : 'text-gray-600'}`}>Start a conversation with our AI assistant and get intelligent, structured responses.</p>
                        <div className="space-y-3">
                          <div className={`p-3 rounded-xl text-left smooth-transition hover-lift ${isDarkMode ? 'bg-gray-800/50' : 'bg-white/80 shadow-sm'}`}>
                            <div className="font-semibold mb-1 text-sm">ðŸ’¡ Ask anything</div>
                            <div className={`text-xs ${isDarkMode ? 'text-gray-400' : 'text-gray-500'}`}>Get detailed answers with structured data</div>
                          </div>
                          <div className={`p-3 rounded-xl text-left smooth-transition hover-lift ${isDarkMode ? 'bg-gray-800/50' : 'bg-white/80 shadow-sm'}`}>
                            <div className="font-semibold mb-1 text-sm">ðŸ“Š Structured responses</div>
                            <div className={`text-xs ${isDarkMode ? 'text-gray-400' : 'text-gray-500'}`}>Receive information in beautiful tables</div>
                          </div>
                        </div>
                      </div>
                    </div>
                  } />
                  <Route path="/chat/:sessionId" element={<ChatWindow isDarkMode={isDarkMode} isMobile={isMobile} isSidebarOpen={isSidebarOpen} />} />
                </Routes>
              </div>
            </div>
          </main>
        </div>

      </div>
    </Router>
  );
}

export default App;
