import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Sidebar from './components/Sidebar';
import ChatWindow from './components/ChatWindow';
import ThemeToggle from './components/ThemeToggle';
import { useMobile } from './hooks/useMobile';

function App() {
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const isMobile = useMobile();

  useEffect(() => {
    const savedTheme = localStorage.getItem('theme');
    if (savedTheme === 'dark') {
      setIsDarkMode(true);
      document.documentElement.classList.add('dark');
    } else {
      setIsDarkMode(false);
      document.documentElement.classList.remove('dark');
    }

    if (!isMobile) {
      setIsSidebarOpen(true);
    }
  }, [isMobile]);

  const toggleTheme = () => {
    const newTheme = !isDarkMode;
    setIsDarkMode(newTheme);
    if (newTheme) {
      document.documentElement.classList.add('dark');
      localStorage.setItem('theme', 'dark');
    } else {
      document.documentElement.classList.remove('dark');
      localStorage.setItem('theme', 'light');
    }
  };

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const closeSidebar = () => {
    if (isMobile) {
      setIsSidebarOpen(false);
    }
  };

  const gridTemplate = isSidebarOpen && !isMobile ? '20rem 1fr' : '0 1fr';

  return (
    <Router>
      <div className={`min-h-screen w-full transition-colors duration-500 ${isDarkMode ? 'bg-gradient-to-br from-gray-900 via-gray-800 to-gray-900 text-white' : 'bg-gradient-to-br from-blue-50 via-white to-purple-50 text-gray-900'}`}>
        <div className="app-grid h-screen w-full overflow-hidden layout-container" style={{ gridTemplateColumns: gridTemplate }}>
          <Sidebar
            isOpen={isSidebarOpen}
            onToggle={toggleSidebar}
            onClose={closeSidebar}
            isDarkMode={isDarkMode}
            isMobile={isMobile}
          />
          <div className="main-column flex-1 flex flex-col w-full min-w-0 transition-all duration-300">
            <header className={`glass border-b ${isDarkMode ? 'border-gray-700' : 'border-gray-200'} p-4 flex items-center sticky top-0 z-40 w-full relative`}>
              <div className="flex items-center space-x-3">
                <button
                  onClick={toggleSidebar}
                  className={`p-3 rounded-2xl smooth-transition hover-lift ${isDarkMode ? 'bg-gray-700 hover:bg-gray-600 text-white' : 'bg-white hover:bg-gray-100 text-gray-700 shadow-sm'}`}
                  aria-label="Toggle sidebar"
                >
                  <div className="w-5 h-5 flex flex-col justify-center items-center">
                    <div className="w-4 h-0.5 bg-current rounded-full transition-all duration-300"></div>
                    <div className="w-4 h-0.5 bg-current rounded-full transition-all duration-300 mt-1"></div>
                  </div>
                </button>
                <div className="flex items-center space-x-3">
                  <div className={`w-8 h-8 rounded-2xl flex items-center justify-center font-bold text-sm ${isDarkMode ? 'bg-gradient-to-r from-purple-500 to-blue-500 text-white shadow-lg' : 'bg-gradient-to-r from-blue-500 to-purple-500 text-white shadow-lg'}`}>
                    L
                  </div>
                </div>
              </div>

              <div className="absolute left-1/2 transform -translate-x-1/2 pointer-events-none">
                <h1 className="text-xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent pointer-events-none">
                  Lumibyte AI
                </h1>
              </div>

              <div className="ml-auto flex items-center space-x-3">
                <div className={`px-3 py-1 rounded-full text-xs font-medium ${isDarkMode ? 'bg-green-500/20 text-green-400' : 'bg-green-100 text-green-700'}`}>
                  Online
                </div>
                <ThemeToggle isDarkMode={isDarkMode} toggleTheme={toggleTheme} />
              </div>
            </header>

            <main className="flex-1 overflow-hidden w-full">
              <Routes>
                <Route path="/" element={
                  <div className="flex items-center justify-center h-full w-full p-4">
                    <div className="text-center max-w-md w-full mx-auto animate-fade-in">
                      <div className="floating mb-8">
                        <div className={`w-24 h-24 mx-auto rounded-3xl flex items-center justify-center text-4xl ${isDarkMode ? 'bg-gradient-to-r from-purple-500 to-blue-500 text-white shadow-2xl' : 'bg-gradient-to-r from-blue-500 to-purple-500 text-white shadow-2xl'}`}>
                          ðŸ’¬
                        </div>
                      </div>
                      <h2 className="text-3xl font-bold mb-4 text-gradient">
                        Welcome to Lumibyte
                      </h2>
                      <p className={`text-lg mb-8 ${isDarkMode ? 'text-gray-300' : 'text-gray-600'}`}>
                        Start a conversation with our AI assistant and get intelligent, structured responses.
                      </p>
                      <div className="space-y-4">
                        <div className={`p-4 rounded-2xl text-left smooth-transition hover-lift ${isDarkMode ? 'bg-gray-800/50' : 'bg-white/80 shadow-sm'}`}>
                          <div className="font-semibold mb-1">ðŸ’¡ Ask anything</div>
                          <div className={`text-sm ${isDarkMode ? 'text-gray-400' : 'text-gray-500'}`}>
                            Get detailed answers with structured data
                          </div>
                        </div>
                        <div className={`p-4 rounded-2xl text-left smooth-transition hover-lift ${isDarkMode ? 'bg-gray-800/50' : 'bg-white/80 shadow-sm'}`}>
                          <div className="font-semibold mb-1">ðŸ“Š Structured responses</div>
                          <div className={`text-sm ${isDarkMode ? 'text-gray-400' : 'text-gray-500'}`}>
                            Receive information in beautiful tables
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                } />
                <Route path="/chat/:sessionId" element={
                  <ChatWindow
                    isDarkMode={isDarkMode}
                    isMobile={isMobile}
                    isSidebarOpen={isSidebarOpen}
                  />
                } />
              </Routes>
            </main>
          </div>
        </div>
      </div>
    </Router>
  );
}

export default App;
