import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Sidebar from './components/Sidebar';
import ChatWindow from './components/ChatWindow';
import ThemeToggle from './components/ThemeToggle';

function App() {
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);

  useEffect(() => {
    const savedTheme = localStorage.getItem('theme');
    if (savedTheme === 'dark') {
      setIsDarkMode(true);
      document.documentElement.classList.add('dark');
    } else {
      setIsDarkMode(false);
      document.documentElement.classList.remove('dark');
    }
  }, []);

  const toggleTheme = () => {
    const newTheme = !isDarkMode;
    setIsDarkMode(newTheme);
    if (newTheme) {
      document.documentElement.classList.add('dark');
      localStorage.setItem('theme', 'dark');
    } else {
      document.documentElement.classList.remove('dark');
      localStorage.setItem('theme', 'light');
    }
  };

  return (
    <Router>
      <div className={`min-h-screen transition-colors duration-200 ${
        isDarkMode 
          ? 'bg-gray-900 text-white' 
          : 'bg-gray-50 text-gray-900'
      }`}>
        <div className="flex h-screen">
          <Sidebar 
            isOpen={isSidebarOpen} 
            onToggle={() => setIsSidebarOpen(!isSidebarOpen)}
            isDarkMode={isDarkMode}
          />
          
          <div className={`flex-1 flex flex-col transition-all duration-300 ${
            isSidebarOpen ? 'md:ml-64' : 'ml-0'
          }`}>
            <header className={`border-b ${
              isDarkMode ? 'border-gray-700 bg-gray-800' : 'border-gray-200 bg-white'
            } p-4 flex justify-between items-center`}>
              <button
                onClick={() => setIsSidebarOpen(!isSidebarOpen)}
                className={`p-2 rounded-lg ${
                  isDarkMode ? 'hover:bg-gray-700' : 'hover:bg-gray-200'
                }`}
              >
                â˜°
              </button>
              <h1 className="text-xl font-semibold">Lumibyte Chat</h1>
              <ThemeToggle isDarkMode={isDarkMode} toggleTheme={toggleTheme} />
            </header>

            <main className="flex-1 overflow-hidden">
              <Routes>
                <Route path="/" element={
                  <div className="flex items-center justify-center h-full">
                    <div className={`text-center p-8 rounded-lg ${
                      isDarkMode ? 'bg-gray-800' : 'bg-white'
                    } shadow-lg`}>
                      <h2 className="text-2xl font-bold mb-4">Welcome to Lumibyte Chat</h2>
                      <p className="mb-4">Start a new conversation or select an existing one from the sidebar</p>
                    </div>
                  </div>
                } />
                <Route path="/chat/:sessionId" element={
                  <ChatWindow isDarkMode={isDarkMode} />
                } />
              </Routes>
            </main>
          </div>
        </div>
      </div>
    </Router>
  );
}

export default App;
