import React, { useState, useEffect, useRef } from 'react';
import { useParams } from 'react-router-dom';
import ChatInput from './ChatInput';
import TableResponse from './TableResponse';
import AnswerFeedback from './AnswerFeedback';

const ChatWindow = ({ isDarkMode }) => {
  const [messages, setMessages] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const { sessionId } = useParams();
  const messagesEndRef = useRef(null);

  useEffect(() => {
    if (sessionId) {
      fetchSessionHistory();
    }
  }, [sessionId]);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const fetchSessionHistory = async () => {
    try {
      const response = await fetch(`http://localhost:5000/api/session/${sessionId}`);
      const data = await response.json();
      setMessages(data);
    } catch (error) {
      console.error('Error fetching session history:', error);
    }
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const handleSendMessage = async (message) => {
    if (!message.trim()) return;

    const userMessage = { type: 'user', content: message };
    setMessages(prev => [...prev, userMessage]);
    setIsLoading(true);

    try {
      const response = await fetch(`http://localhost:5000/api/chat/${sessionId}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ message }),
      });
      
      const assistantResponse = await response.json();
      setMessages(prev => [...prev, assistantResponse]);
    } catch (error) {
      console.error('Error sending message:', error);
      const errorMessage = {
        type: 'assistant',
        content: 'Sorry, I encountered an error. Please try again.',
        tableData: null
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  if (!sessionId) {
    return (
      <div className="flex items-center justify-center h-full">
        <div className="text-center">
          <h2 className="text-2xl font-bold mb-4">No Session Selected</h2>
          <p>Please start a new chat or select an existing one.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="flex flex-col h-full">
      <div className="flex-1 overflow-y-auto p-4 space-y-6">
        {messages.length === 0 ? (
          <div className="flex items-center justify-center h-full">
            <div className={`text-center p-8 rounded-lg max-w-md ${
              isDarkMode ? 'bg-gray-800' : 'bg-white'
            } shadow-lg`}>
              <h3 className="text-xl font-semibold mb-2">Start a Conversation</h3>
              <p className={`${
                isDarkMode ? 'text-gray-400' : 'text-gray-600'
              }`}>
                Ask me anything and I'll provide structured responses with tabular data.
              </p>
            </div>
          </div>
        ) : (
          messages.map((message, index) => (
            <div
              key={index}
              className={`max-w-4xl mx-auto w-full ${
                message.type === 'user' ? 'ml-auto' : 'mr-auto'
              }`}
            >
              <div className={`rounded-lg p-4 ${
                message.type === 'user'
                  ? isDarkMode
                    ? 'bg-blue-600 text-white'
                    : 'bg-blue-500 text-white'
                  : isDarkMode
                  ? 'bg-gray-800 border border-gray-700'
                  : 'bg-white border border-gray-200'
              }`}>
                <div className="whitespace-pre-wrap">{message.content}</div>
                
                {message.tableData && (
                  <div className="mt-4">
                    <TableResponse data={message.tableData} isDarkMode={isDarkMode} />
                  </div>
                )}
                
                {message.type === 'assistant' && (
                  <div className="mt-3 pt-3 border-t border-gray-300 dark:border-gray-600">
                    <AnswerFeedback />
                  </div>
                )}
              </div>
            </div>
          ))
        )}
        
        {isLoading && (
          <div className="max-w-4xl mx-auto w-full">
            <div className={`rounded-lg p-4 ${
              isDarkMode
                ? 'bg-gray-800 border border-gray-700'
                : 'bg-white border border-gray-200'
            }`}>
              <div className="flex space-x-2">
                <div className="w-2 h-2 rounded-full bg-gray-400 animate-bounce"></div>
                <div className="w-2 h-2 rounded-full bg-gray-400 animate-bounce" style={{ animationDelay: '0.2s' }}></div>
                <div className="w-2 h-2 rounded-full bg-gray-400 animate-bounce" style={{ animationDelay: '0.4s' }}></div>
              </div>
            </div>
          </div>
        )}
        
        <div ref={messagesEndRef} />
      </div>

      <div className={`border-t ${
        isDarkMode ? 'border-gray-700' : 'border-gray-200'
      } p-4`}>
        <ChatInput onSendMessage={handleSendMessage} isDarkMode={isDarkMode} />
      </div>
    </div>
  );
};

export default ChatWindow;
