import React, { useState } from 'react';

const AnswerFeedback = () => {
  const [feedback, setFeedback] = useState(null);

  const handleFeedback = (type) => {
    setFeedback(type);
  };

  return (
    <div className="flex items-center space-x-2">
      <span className={`text-sm ${
        feedback ? 'text-gray-500' : 'text-gray-400'
      }`}>Was this helpful?</span>
      <button
        onClick={() => handleFeedback('like')}
        className={`p-1 rounded transition-colors ${
          feedback === 'like'
            ? 'text-green-500 bg-green-100 dark:bg-green-900'
            : 'text-gray-400 hover:text-green-500 hover:bg-gray-100 dark:hover:bg-gray-700'
        }`}
        disabled={feedback !== null}
      >
        üëç
      </button>
      <button
        onClick={() => handleFeedback('dislike')}
        className={`p-1 rounded transition-colors ${
          feedback === 'dislike'
            ? 'text-red-500 bg-red-100 dark:bg-red-900'
            : 'text-gray-400 hover:text-red-500 hover:bg-gray-100 dark:hover:bg-gray-700'
        }`}
        disabled={feedback !== null}
      >
        üëé
      </button>
    </div>
  );
};

export default AnswerFeedback;
