import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

const Sidebar = ({ isOpen, onToggle, isDarkMode }) => {
  const [sessions, setSessions] = useState([]);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    fetchSessions();
  }, []);

  const fetchSessions = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/sessions');
      const data = await response.json();
      setSessions(data);
    } catch (error) {
      console.error('Error fetching sessions:', error);
    }
  };

  const handleNewChat = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/new-chat');
      const data = await response.json();
      navigate(`/chat/${data.sessionId}`);
      fetchSessions();
    } catch (error) {
      console.error('Error creating new chat:', error);
    }
  };

  const isActiveSession = (sessionId) => {
    return location.pathname === `/chat/${sessionId}`;
  };

  return (
    <>
      <div className={`fixed inset-0 bg-black bg-opacity-50 z-20 md:hidden ${
        isOpen ? 'block' : 'hidden'
      }`} onClick={onToggle}></div>
      
      <div className={`fixed md:static inset-y-0 left-0 z-30 w-64 transform transition-transform duration-300 ease-in-out ${
        isOpen ? 'translate-x-0' : '-translate-x-full'
      } md:translate-x-0 flex flex-col ${
        isDarkMode ? 'bg-gray-800 border-r border-gray-700' : 'bg-white border-r border-gray-200'
      }`}>
        <div className="p-4">
          <button
            onClick={handleNewChat}
            className={`w-full py-3 px-4 rounded-lg font-semibold transition-colors ${
              isDarkMode
                ? 'bg-blue-600 hover:bg-blue-700 text-white'
                : 'bg-blue-500 hover:bg-blue-600 text-white'
            }`}
          >
            + New Chat
          </button>
        </div>

        <div className="flex-1 overflow-y-auto">
          <div className="p-4">
            <h3 className={`text-sm font-semibold mb-2 ${
              isDarkMode ? 'text-gray-400' : 'text-gray-500'
            }`}>Recent Chats</h3>
            <div className="space-y-1">
              {sessions.map((session) => (
                <button
                  key={session.id}
                  onClick={() => navigate(`/chat/${session.id}`)}
                  className={`w-full text-left p-3 rounded-lg transition-colors ${
                    isActiveSession(session.id)
                      ? isDarkMode
                        ? 'bg-gray-700 text-white'
                        : 'bg-blue-100 text-blue-900'
                      : isDarkMode
                      ? 'hover:bg-gray-700 text-gray-300'
                      : 'hover:bg-gray-100 text-gray-700'
                  }`}
                >
                  <div className="font-medium truncate">{session.title}</div>
                  <div className={`text-xs ${
                    isDarkMode ? 'text-gray-500' : 'text-gray-400'
                  }`}>
                    {new Date(session.timestamp).toLocaleDateString()}
                  </div>
                </button>
              ))}
            </div>
          </div>
        </div>

        <div className={`p-4 border-t ${
          isDarkMode ? 'border-gray-700' : 'border-gray-200'
        }`}>
          <div className="flex items-center space-x-3">
            <div className={`w-8 h-8 rounded-full flex items-center justify-center font-semibold ${
              isDarkMode ? 'bg-blue-600 text-white' : 'bg-blue-500 text-white'
            }`}>
              U
            </div>
            <div>
              <div className={`font-medium ${
                isDarkMode ? 'text-white' : 'text-gray-900'
              }`}>User</div>
              <div className={`text-xs ${
                isDarkMode ? 'text-gray-400' : 'text-gray-500'
              }`}>Free Plan</div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Sidebar;
