import React, { useState, useEffect, useRef } from 'react';
import { useParams } from 'react-router-dom';
import ChatInput from './ChatInput';
import TableResponse from './TableResponse';
import AnswerFeedback from './AnswerFeedback';

const ChatWindow = ({ isDarkMode, isMobile, isSidebarOpen }) => {
  const [messages, setMessages] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const { sessionId } = useParams();
  const messagesEndRef = useRef(null);
  const chatContainerRef = useRef(null);

  useEffect(() => {
    if (sessionId) {
      fetchSessionHistory();
    } else {
      setMessages([]);
    }
  }, [sessionId]);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const fetchSessionHistory = async () => {
    try {
      const response = await fetch(`http://localhost:5000/api/session/${sessionId}`);
      const data = await response.json();
      setMessages(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error('Error fetching session history:', error);
      setMessages([]);
    }
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({
      behavior: 'smooth',
      block: 'end'
    });
  };

  const handleSendMessage = async (message) => {
    if (!message.trim()) return;

    const userMessage = {
      type: 'user',
      content: message,
      timestamp: new Date().toISOString()
    };
    setMessages(prev => [...prev, userMessage]);
    setIsLoading(true);

    try {
      const response = await fetch(`http://localhost:5000/api/chat/${sessionId}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ message }),
      });

      const assistantResponse = await response.json();
      setMessages(prev => [...prev, { ...assistantResponse, timestamp: new Date().toISOString() }]);
    } catch (error) {
      console.error('Error sending message:', error);
      const errorMessage = {
        type: 'assistant',
        content: 'Sorry, I encountered an error. Please try again.',
        tableData: null,
        timestamp: new Date().toISOString()
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="flex flex-col h-full w-full relative">
      <div
        ref={chatContainerRef}
        className="flex-1 overflow-y-auto p-6 space-y-6 w-full"
      >
        {(messages.length === 0) ? (
          <div className="flex items-center justify-center h-full w-full px-4">
            <div className={`text-center p-8 rounded-3xl max-w-xl w-full mx-auto ${isDarkMode ? 'bg-gray-800/50' : 'bg-white/90'} shadow-xl backdrop-blur-sm animate-fade-in`}>
              <div className="floating mb-6">
                <div className={`w-20 h-20 mx-auto rounded-2xl flex items-center justify-center text-3xl ${isDarkMode ? 'bg-gradient-to-r from-purple-500 to-blue-500 text-white' : 'bg-gradient-to-r from-blue-500 to-purple-500 text-white'} shadow-2xl`}>
                  ðŸ’¬
                </div>
              </div>
              <h3 className="text-2xl font-bold mb-3 text-gradient">Start Conversation</h3>
              <p className={`mb-6 ${isDarkMode ? 'text-gray-300' : 'text-gray-600'}`}>
                Ask me anything and I'll provide intelligent responses with structured data.
              </p>
              <div className="grid grid-cols-1 gap-3 text-left">
                {[
                  "Show me project metrics",
                  "What are the system requirements?",
                  "Display team performance data",
                  "List available features"
                ].map((suggestion, index) => (
                  <div
                    key={index}
                    className={`p-3 rounded-xl smooth-transition hover-lift cursor-pointer ${isDarkMode ? 'bg-gray-700/50 hover:bg-gray-600/50' : 'bg-gray-50 hover:bg-white'}`}
                    onClick={() => handleSendMessage(suggestion)}
                  >
                    <div className="text-sm">{suggestion}</div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        ) : (
          messages.map((message, index) => (
            <div
              key={index}
              className={`max-w-4xl w-full mx-auto message-enter ${message.type === 'user' ? 'ml-auto' : 'mr-auto'}`}
              style={{ animationDelay: `${index * 0.06}s` }}
            >
              <div className={`rounded-3xl p-6 smooth-transition hover-lift ${message.type === 'user' ? isDarkMode ? 'bg-gradient-to-r from-blue-500 to-purple-500 text-white shadow-2xl' : 'bg-gradient-to-r from-blue-500 to-purple-500 text-white shadow-2xl' : isDarkMode ? 'bg-gray-800/80 border border-gray-700 shadow-xl' : 'bg-white/80 border border-gray-200 shadow-xl'}`}>
                <div className="flex items-center space-x-3 mb-4">
                  <div className={`w-8 h-8 rounded-xl flex items-center justify-center text-sm font-bold ${message.type === 'user' ? 'bg-white/20 text-white' : isDarkMode ? 'bg-gradient-to-r from-green-500 to-emerald-500 text-white' : 'bg-gradient-to-r from-green-500 to-emerald-500 text-white'}`}>
                    {message.type === 'user' ? 'U' : 'AI'}
                  </div>
                  <div className="flex-1">
                    <div className="font-semibold">
                      {message.type === 'user' ? 'You' : 'Lumibyte AI'}
                    </div>
                    <div className={`text-xs ${message.type === 'user' ? 'text-white/70' : isDarkMode ? 'text-gray-400' : 'text-gray-500'}`}>
                      {new Date(message.timestamp).toLocaleTimeString()}
                    </div>
                  </div>
                </div>

                <div className="whitespace-pre-wrap leading-relaxed">
                  {message.content}
                </div>

                {message.tableData && (
                  <div className="mt-6 animate-fade-in">
                    <TableResponse data={message.tableData} isDarkMode={isDarkMode} />
                  </div>
                )}

                {message.type === 'assistant' && (
                  <div className="mt-6 pt-4 border-t border-gray-300 dark:border-gray-600">
                    <AnswerFeedback isDarkMode={isDarkMode} />
                  </div>
                )}
              </div>
            </div>
          ))
        )}

        <div ref={messagesEndRef} className="h-4 w-full" />
      </div>

      <div className="sticky bottom-0 left-0 right-0 px-4 pt-4 pb-6 backdrop-blur-lg bg-gradient-to-t from-transparent to-transparent z-50">
        <div className="max-w-4xl mx-auto w-full">
          <ChatInput onSendMessage={handleSendMessage} isDarkMode={isDarkMode} isMobile={isMobile} />
        </div>
      </div>
    </div>
  );
};

export default ChatWindow;
